for l in a:
    links.append(base + l.get('href'))

doc = 0

for l in links:
    try:
        gov = requests.get(l)
    except Exception as e:
        print(l, e)

    soup = BeautifulSoup(gov.content, 'html.parser')

    text = ''

    article = soup.find('article')
    for string in article.strings:
        txt = repr(string).strip()
        txt = txt.replace("'", '')
        txt = txt.replace('"', '')
        txt = txt.replace('\\n', '\n')
        txt = txt.replace('\\s', '')
        txt = txt.replace('\\t', '')
        txt = txt.replace('\\xa0', '')
        txt = txt.replace('\\x96', '')
        txt = txt.replace('\\r', '')

        if len(txt) == 0:
            continue

        text += txt

    text = text.replace('U.S.', 'USA')
    text = text.replace('.', '.\n')
    text = text.replace('.\ngov', '.gov')
    text = re.sub(r"(\d+)\.\n(\d+)", r"\1.\2", text)
    lines = text.splitlines()

    final = []

    for line in lines:
        line = line.strip()
        if line == '':
            continue
        final.append(line)

    text = "\n".join(final)

    text = l + '\n' + text
    text = text.encode('ascii', 'ignore')

    f = open(f'{OUTDIR}/{doc}.txt', "wb")
    f.write(text)
    f.close()

    doc += 1
